set serveroutput on
/
truncate table KC_ROLE_BOOTSTRAP
/
commit
/
declare
li_count number;
  cursor c_data is
    select role_id, role_nm, nmspc_cd,desc_txt,kim_typ_id,actv_ind,last_updt_dt 
    from KC_ROLE_BOOTSTRAP
    where kim_typ_id in (select kim_typ_id from krim_typ_t where nm like 'Derived Role:%');
  r_data c_data%rowtype;
begin

  open c_data;
  loop
  fetch c_data into r_data;
  exit when c_data%notfound;
  
  select count(role_id) into li_count from KRIM_ROLE_T
  where role_nm = r_data.role_nm
  and   nmspc_cd = r_data.nmspc_cd;
  
  if li_count = 0 then  
			VALUES(KRIM_ROLE_ID_S.NEXTVAL,SYS_GUID(),1,r_data.ROLE_NM,r_data.NMSPC_CD,r_data.DESC_TXT,r_data.KIM_TYP_ID,r_data.ACTV_IND,r_data.LAST_UPDT_DT);	
      
      dbms_output.put_line('Inserted derived role '||r_data.ROLE_NM||' with nmspc_cd '||r_data.NMSPC_CD);
  
  end if;		
  end loop;
  close c_data;

end;
/
